<?php/** * Created by: * User: Adam * Date: 09/02/2020 * Time: 2:08 PM */namespace app\modules\inventory\models;use Yii;use app\smartadmin\ActiveForm;use yii\helpers\Html;use yii\helpers\ArrayHelper;class FormIncoming extends Incoming{    public function validate($attributeNames = null, $clearErrors = true)    {        $outParent = parent::validate($attributeNames, $clearErrors);        $out = true;                return (object)['val' => $outParent && $out, 'parent' => $outParent, 'detail' => $out];    }    public function save($runValidation = true, $attributeNames = null)    {        $this->date=date('Y-m-d H:m:s',strtotime($this->date));        $outParent = parent::save($runValidation, $attributeNames);        $out = true;                return $outParent && $out;    }       /**     * @param array $data     * @param null $formName     * @return bool     */    public function load($data, $formName = null)    {        $data = $data == null ? [] : $data;        $ld = parent::load($data, $formName);        return $ld;    }   }